import React, {Component, Fragment} from "react";
import './drawing-tool.css';

class DrawingTool extends Component {

    render() {
        return (
            <Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-12">
                            <form>
                                <table id="drawing-table" className="drawing-table">
                                    <tbody>{this.generateTileMap()}</tbody>
                                </table>
                            </form>
                        </div>

                        <div className={'col-12 mt-4'}>
                            <button className={'btn btn-primary'} onClick={() => this.setState({type: 'straight'})}>Straight</button> &nbsp;
                            <button className={'btn btn-primary'} onClick={() => this.setState({type: 'topLeft'})}>Top Left</button> &nbsp;
                            <button className={'btn btn-primary'} onClick={() => this.setState({type: 'topRight'})}>Top Right</button> &nbsp;
                            <button className={'btn btn-primary'} onClick={() => this.setState({type: 'bottomLeft'})}>Bottom Left</button> &nbsp;
                            <button className={'btn btn-primary'} onClick={() => this.setState({type: 'bottomRight'})}>Bottom Right</button> &nbsp;
                            <button className={'btn btn-primary'} onClick={() => this.setState({type: 'erase'})}>Erase</button>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }

}

export default DrawingTool;
