import React, {Component, Fragment} from "react";
import {Modal, Button} from "react-bootstrap";
import './drawing-tool.css';

class DrawingTool extends Component {
    constructor() {
        super();
        this.state = {
            show: false,
            close: false,
            coordinate: [0, 0],
            point: null
        }
    }
    componentDidMount() {
        this.generateTileMap();
    }

    generateTileMap() {
        let rows = [];
        for (let y = 0; y < 10; y++) {
            const column = [];
            for (let x = 0; x < 10; x++) {
                column.push(
                    <td
                        key={x}
                        className={"text-center"}
                        onClick={this.drawPath}
                        onDoubleClick={() => this.setState({show: true, close: false, coordinate: [y, x]})}
                        id={y + '' + x}
                    >
                    </td>
                );
            }
            rows.push(<tr key={y}>{column}</tr>);
        }
        return rows;
    }

    drawPath() {

    }

    selectPoint(e) {
        e.preventDefault();
        console.log(e);
    }

    render() {
        return (
            <Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-12">
                            <div className="row">
                                <div className="col-6">
                                    <div className="card">
                                        <div className="card-header">Drawing Tool</div>
                                        <div className='card-body'>
                                            <table className="drawing-table">
                                                <tbody>{this.generateTileMap()}</tbody>
                                            </table>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-4">Hi</div>
                            </div>
                        </div>
                    </div>
                </div>

                {/*point select modal*/}
                <Modal show={this.state.show}>
                    <Modal.Header>
                        <Modal.Title>Select point</Modal.Title>
                    </Modal.Header>
                    <form>
                        <Modal.Body>
                            <div className="form-group row">
                                <label htmlFor="staticEmail" className="col-sm-4 col-form-label">Point Name</label>
                                <div className="col-sm-8">
                                    <select className="form-control" name="point-name" onChange={e => this.setState({point: e.target.value})}>
                                        <option Value="" selected default>Select point</option>
                                        <option value="0">Arrival</option>
                                        <option value="1">Menu</option>
                                        <option value="2">Cashier</option>
                                        <option value="3">Pickup</option>
                                    </select>
                                </div>
                            </div>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => this.setState({show: false, close: true})}>Close</Button>
                            <Button variant="primary">Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal>
            </Fragment>
        );
    }

}

export default DrawingTool;
