import React, {Component, Fragment} from "react";
import './drawing-tool.css';

class DrawingTool extends Component {
    componentDidMount() {
        this.generateTileMap()
    }

    drawPath() {

    }

    selectPoint() {

    }

    generateTileMap() {
        let rows = [];
        for (let y = 0; y < 10; y++) {
            const column = [];
            for (let x = 0; x < 10; x++) {
                column.push(
                    <td
                        key={x}
                        className={"text-center"}
                        onClick={this.drawPath}
                        onDoubleClick={this.selectPoint}
                        id={y + '' + x}
                    >
                    </td>
                );
            }
            rows.push(<tr key={y}>{column}</tr>);
        }
        return rows;
    }

    render() {
        return (
            <Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-12">
                            <div className="card">
                                <div className="card-header">Drawing Tool</div>
                                <div className='card-body'>
                                    <table className="drawing-table">
                                        <tbody>{this.generateTileMap()}</tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/*bootstrap modal to set points*/}
                <div className="modal" tabIndex="-1" role="dialog" id="set-point-modal">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Modal title</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body">
                                <form>
                                    <div className="form-group row">
                                        <label className="col-sm-2 col-form-label">Point Name</label>
                                        <div className="col-sm-10">
                                            <input type="text" className="form-control-plaintext" name="point-name"/>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary">Add</button>
                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }

}

export default DrawingTool;
