import React, {Component, Fragment} from "react";
import {Modal, Button} from "react-bootstrap";
import './drawing-tool.css';

const blockStyles = {
    0: {
        style: {
            width: "30px",
            height: "30px",
            borderRadius: "0px 0px 0px 0px",
            backgroundColor: "#607d8b",
        },
        name: "Straight",
    },
    1: {
        style: {
            width: "30px",
            height: "30px",
            borderRadius: "0px 0px 00px 0px",
            backgroundColor: "#fff",
            border: "solid thin #b0bec5",
        },
        name: "Delete",
    },
    2: {
        style: {
            width: "30px",
            height: "30px",
            borderRadius: "0px 50px 0px 0px",
            backgroundColor: "#607d8b",
        },
        name: "Top Left",
    },
    3: {
        style: {
            width: "30px",
            height: "30px",
            borderRadius: "50px 0px 0px 0px",
            backgroundColor: "#607d8b",
        },
        name: "Top Right",
    },
    4: {
        style: {
            width: "30px",
            height: "30px",
            borderRadius: "0px 0px 0px 50px",
            backgroundColor: "#607d8b",
        },
        name: "Bottom Left",
    },
    5: {
        style: {
            width: "30px",
            height: "30px",
            borderRadius: "0px 0px 50px 0px",
            backgroundColor: "#607d8b",
        },
        name: "Bottom Right",
    },
}

class DrawingTool extends Component {
    constructor() {
        super();
        this.columnRef = React.createRef();
        this.state = {
            show: false,
            coordinate: [0, 0],
            point: null,
            path: {},
            remainingBlocks: 0,
            blockType: null,
        }
    }

    componentDidMount() {
    }

    generateTileMap() {
        let rows = [];
        for (let y = 0; y < 10; y++) {
            const column = [];
            for (let x = 0; x < 10; x++) {
                column.push(
                    <td
                        key={x}
                        className={"text-center"}
                        onClick={this.drawPath}
                        onDoubleClick={() => this.setState({show: true, close: false, coordinate: [y, x]})}
                        id={y + '' + x}
                        ref={this.columnRef}
                    >
                    </td>
                );
            }
            rows.push(<tr key={y}>{column}</tr>);
        }
        return rows;
    }

    carsPerLane() {
        let options = [];
        for (let cars = 1; cars <= 20; cars++) {
            options.push(<option value={cars}>{cars}</option>)
        }
        return options
    }

    generatesBlocks = () => {
        let rows = [];
        let count = 0
        for(let row = 0; row < 3; row++){
            let columns = [];
            for(let column = 0; column < 2; column++) {
                columns.push(
                    <td className="text-center" onClick={this.selectBlock} ref={this.blockRef} id={count}>
                        <label style={blockStyles[count]['style']} className="mb-0"></label>
                        <p>{count}</p>
                    </td>
                )
                count++;
            }
            rows.push(<tr>{columns}</tr>)
        }
        return rows;
    }

    selectBlock = (e) => {
        console.log()
    }

    definePoints(e) {

    }

    drawPath = () => {
        let columnId = this.columnRef.current.id;
    }

    render() {
        return (
            <Fragment>
                <div className="container">
                    <div className="card">
                        <div className="card-header">Drawing Tool</div>
                        <div className='card-body'>
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-6">
                                            <table className="drawing-table">
                                                <tbody>
                                                    <tbody>{this.generateTileMap()}</tbody>
                                                </tbody>
                                            </table>
                                        </div>
                                        <div className="col-6">
                                            <div className="form-group row">
                                                <label htmlFor="staticEmail"
                                                       className="col-sm-4 col-form-label">Lane Number</label>
                                                <div className="col-sm-8">
                                                    <select className="form-control" name="point-name"
                                                            onChange={e => this.setState({point: e.target.value})}>
                                                        <option value="" selected default>Select lane</option>
                                                        <option value="1">1</option>
                                                        <option value="2">2</option>
                                                        <option value="3">3</option>
                                                        <option value="4">4</option>
                                                        <option value="5">5</option>
                                                    </select>
                                                </div>
                                            </div>

                                            <div className="form-group row">
                                                <label htmlFor="staticEmail"
                                                       className="col-sm-4 col-form-label">Cars per lane</label>
                                                <div className="col-sm-8">
                                                    <select className="form-control" name="point-name"
                                                            onChange={e => this.setState({remainingBlocks: e.target.value})}>
                                                        <option value="" selected default>Select number</option>
                                                        {this.carsPerLane()}
                                                    </select>
                                                </div>
                                            </div>

                                            <div className="row mt-4">
                                                <div className='col-sm-4'></div>
                                                <div className="col-sm-8">
                                                    <table
                                                        className="table table-bordered justify-content-center block-table">
                                                        <tbody>
                                                        {this.generatesBlocks()}
                                                        </tbody>
                                                    </table>
                                                </div>
                                            </div>

                                            <div className="row mt-4">
                                                <label htmlFor="staticEmail"
                                                       className="col-sm-4 font-weight-bold">Remaining Blocks</label>
                                                <div className="col-sm-8 font-weight-bold">
                                                    {this.state.remainingBlocks}
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/*point select modal*/}
                <Modal show={this.state.show}>
                    <Modal.Header>
                        <Modal.Title>Select point</Modal.Title>
                    </Modal.Header>
                    <form>
                        <Modal.Body>
                            <div className="form-group row">
                                <label htmlFor="staticEmail" className="col-sm-4 col-form-label">Point Name</label>
                                <div className="col-sm-8">
                                    <select className="form-control" name="point-name"
                                            onChange={e => this.setState({point: e.target.value})}>
                                        <option Value="" selected default>Select point</option>
                                        <option value="0">Arrival</option>
                                        <option value="1">Menu</option>
                                        <option value="2">Cashier</option>
                                        <option value="3">Pickup</option>
                                    </select>
                                </div>
                            </div>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => this.setState({show: false})}>Close</Button>
                            <Button variant="primary" onClick={this.definePoints}>Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal>
            </Fragment>
        );
    }

}

export default DrawingTool;
