import React, {Component, Fragment} from "react";
import './drawing-tool.css';

class DrawingTool extends Component {
    generateTileMap() {
        // generate tile map
        let rows = [];
        for (let y = 0; y < 10; y++) {
            const column = [];
            for (let x = 0; x < 10; x++) {
                column.push(
                    <td
                        key={x}
                        style={{ width: "50px", height: "50px", border: "1px dotted #ccc"}}
                        className={"text-center"}
                        onClick={this.changeColor}
                        id={y + '' + x}
                    >
                        {0}
                    </td>
                );
            }
            rows.push(<tr key={y}>{column}</tr>);
        }
        return rows;
    }

    render() {
        return (
            <Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-12">
                            <div className="box">
                                <div className='box-body'>
                                    <table id="drawing-table" className="drawing-table">
                                        <tbody>{this.generateTileMap()}</tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }

}

export default DrawingTool;
